export function twoSelectedBeside(i, state) {
    var selected = 0;
    if(i === 0) {
        if(state[1] === "selected" && state[4] === "selected") {
            return true;
        }
    }
    else if(i === 4) {
        if(state[0] === "selected") {
            return true;
        }
    }
    return false;
}
