import React from "react";
import ReactDOM from "react-dom";

//Style files.
import "./style/index.css";
import { myColors } from "./style/colors.js";
import { HorizontalSpace, VerticalSpace, Circle } from "./style/styles.js";

//Logic files.
import { circleStates } from "./logic.js";

class Board extends React.Component {
    renderCircle(i) {
      return (
        <Circle
          value={this.props.circleValues[i]}
          color={this.props.circleColors[i]}
          onClick={() => this.props.onClick(i)}
        />
      );
    }

    render() {
      return (
        <div>
          <div className="game-board">
            <div className="game-row align-left">
              {this.renderCircle(0)}
              <HorizontalSpace />
              {this.renderCircle(1)}
              <HorizontalSpace />
              {this.renderCircle(2)}
              <HorizontalSpace />
              {this.renderCircle(3)}
            </div>
            <VerticalSpace />
            <div className="game-row align-right">
              {this.renderCircle(4)}
              <HorizontalSpace />
              {this.renderCircle(5)}
              <HorizontalSpace />
              {this.renderCircle(6)}
              <HorizontalSpace />
              {this.renderCircle(7)}
            </div>
            <VerticalSpace />
            <div className="game-row align-left">
              {this.renderCircle(8)}
              <HorizontalSpace />
              {this.renderCircle(9)}
              <HorizontalSpace />
              {this.renderCircle(10)}
              <HorizontalSpace />
              {this.renderCircle(11)}
            </div>
            <VerticalSpace />
            <div className="game-row align-right">
              {this.renderCircle(12)}
              <HorizontalSpace />
              {this.renderCircle(13)}
              <HorizontalSpace />
              {this.renderCircle(14)}
              <HorizontalSpace />
              {this.renderCircle(15)}
            </div>
          </div>
          <div style={{ height: "30px" }}></div>
          <div style={{ textAlign: "center", color: "#FFFFFF" }}>
            Find the code here:{" "}
            <a
              style={{ color: "#FFFFFF" }}
              href="https://github.com/AlexanderStewart/circles-web"
            >
              github.com/AlexanderStewart/circles-web
            </a>
          </div>
        </div>
      );
    }
  }

  export default Board;
