import React from "react";

//Imports.
import "../style/index.scss";
import { Circle } from "./circle.js";

const tileXCoordinate = [];
const tileYCoordinate = [];

const dividerWidth = "0.01";
const tileWidth = "0.1875";

tileXCoordinate[0] = dividerWidth;
tileXCoordinate[1] = dividerWidth * 2 + tileWidth;
tileXCoordinate[2] = dividerWidth * 3 + tileWidth * 2;
tileXCoordinate[3] = dividerWidth * 4 + tileWidth * 3;

tileXCoordinate[4] = dividerWidth + tileWidth / 2;
tileXCoordinate[5] = dividerWidth * 2 + tileWidth / 2 + tileWidth;
tileXCoordinate[6] = dividerWidth * 3 + tileWidth / 2 + tileWidth * 2;
tileXCoordinate[7] = dividerWidth * 4 + tileWidth / 2 + tileWidth * 3;

tileXCoordinate[8] = dividerWidth;
tileXCoordinate[9] = dividerWidth * 2 + tileWidth;
tileXCoordinate[10] = dividerWidth * 3 + tileWidth * 2;
tileXCoordinate[11] = dividerWidth * 4 + tileWidth * 3;

tileXCoordinate[12] = dividerWidth + tileWidth / 2;
tileXCoordinate[13] = dividerWidth * 2 + tileWidth / 2 + tileWidth;
tileXCoordinate[14] = dividerWidth * 3 + tileWidth / 2 + tileWidth * 2;
tileXCoordinate[15] = dividerWidth * 4 + tileWidth / 2 + tileWidth * 3;

//Y coordinates of tiles.
tileYCoordinate[0] = dividerWidth;
tileYCoordinate[1] = dividerWidth;
tileYCoordinate[2] = dividerWidth;
tileYCoordinate[3] = dividerWidth;

tileYCoordinate[4] = dividerWidth * 2 + tileWidth;
tileYCoordinate[5] = dividerWidth * 2 + tileWidth;
tileYCoordinate[6] = dividerWidth * 2 + tileWidth;
tileYCoordinate[7] = dividerWidth * 2 + tileWidth;

tileYCoordinate[8] = dividerWidth * 3 + tileWidth * 2;
tileYCoordinate[9] = dividerWidth * 3 + tileWidth * 2;
tileYCoordinate[10] = dividerWidth * 3 + tileWidth * 2;
tileYCoordinate[11] = dividerWidth * 3 + tileWidth * 2;

tileYCoordinate[12] = dividerWidth * 4 + tileWidth * 3;
tileYCoordinate[13] = dividerWidth * 4 + tileWidth * 3;
tileYCoordinate[14] = dividerWidth * 4 + tileWidth * 3;
tileYCoordinate[15] = dividerWidth * 4 + tileWidth * 3;

for (var i = 0; i <= 15; i++) {
    var d = document.getElementById(i.toString);
    d.style.position = "absolute";
    d.style.left = tileXCoordinate[i] + "vw";
    d.style.top = tileYCoordinate[i] + "vw";
}

class Board extends React.Component {
  renderCircle(i) {
    return (
      <Circle
        value={this.props.circleValues[i]}
        color={this.props.circleColors[i]}
        onClick={() => this.props.onClick(i)}
      />
    );
  }

  render() {
    return (
      <div>
        <div className="title">circles</div>
        <div className="content-space"></div>
        <div className="game-board">
          <div className="0"></div>
          <div className="1"></div>
          <div className="2"></div>
          <div className="3"></div>
          <div className="4"></div>
          <div className="5"></div>
          <div className="6"></div>
          <div className="7"></div>
          <div className="8"></div>
          <div className="9"></div>
          <div className="10"></div>
          <div className="11"></div>
          <div className="12"></div>
          <div className="13"></div>
          <div className="14"></div>
          <div className="15"></div>
        </div>
        <div className="content-space"></div>
        <div className="github-link-text">
          find the code here:{" "}
          <a
            className="github-link"
            href="https://github.com/AlexanderStewart/circles-web"
          >
            github.com/alexanderstewart/circles-web
          </a>
        </div>
      </div>
    );
  }
}

export default Board;
